mapper.select:
    SELECT  
    	NVL((SELECT PAYMENT_FL
       	FROM PAYMENT P 
       	WHERE B.BOARD_NO = P.PAYMENT_BOARD
       	AND P.PAYMENT_MEMBER = #{memberNo}
       ), 'N') PAYMENT_FL
	FROM BOARD B
	WHERE B.BOARD_NO = #{boardNo}

SELECT COUNT(*) FROM BOARD
WHERE CLASS_NO = #{classNo}
AND BOARD_DEL_FL= 'N'
