mapper.update:
UPDATE REVIEW
SET REVIEW_CONTENT = #{reviewContent},
REVIEW_WRITE_DATE = CURRENT_DATE,
"NUMBER" = #{number}
WHERE COMMENT_NO = #{commentNo}
AND MEMBER_NO = #{memberNo}

UPDATE REVIEW
SET REVIEW_DEL_FL = 'Y'
WHERE COMMENT_NO = #{commentNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT MEMBER_NO
FROM REVIEW
WHERE COMMENT_NO = #{commentNo}
