mapper.update:
UPDATE "MEMBER"
SET MEMBER_NAME = #{memberName}, MEMBER_TEL = #{memberTel}
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT REVIEW_CONTENT, 
	(SELECT QUERY_TYPE
	FROM QUERY
	WHERE QUERY_NO = ${queryNo}
	), REVIEW_WRITE_DATE
FROM REVIEW
JOIN BOARD USING(BOARD_NO)
WHERE MEMBER_NO = #{memberNo}
