mapper.update:
	UPDATE "TRAINER"
	SET TRAINER_NICKNAME = #{trainerNickname}, 
	TRAINER_TEL = #{trainerTel}
    WHERE TRAINER_NO = #{trainerNo}

mapper.insert:
INSERT INTO "QUALIFICATION"

	SELECT NEXT_QUALIFICATION_NO(), #{item.qualification}, #{item.qualificationDate}, ${item.trainerNo}
	FROM DUAL
